CREATE TABLE `position_content` (
  `position_content_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `position_id` smallint(5) unsigned NOT NULL COMMENT '推荐位id',
  `article_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '文章id',
  `title` varchar(150) NOT NULL COMMENT '文章标题',
  `thumb` varchar(255) NOT NULL DEFAULT '' COMMENT '文章缩略图',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '文章地址',
  `list_order` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态，-1：删除，0：禁用，1：启用',
  PRIMARY KEY (`position_content_id`),
  KEY `position_id` (`position_id`),
  KEY `article_id` (`article_id`)
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 COMMENT='推荐位内容表';
